import React, {Component} from 'react';
import CarouselItem from './CarouselItem';

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: [],
          n: 8,
          moviesDisplayed: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
    }

    handleDisplayMovies = (beginning) => {
        for (let i = beginning; i < this.state.n; i++) {
            this.state.moviesDisplayed.push(this.state.movies[i]); 
        }
    }

    render() {
        this.handleDisplayMovies();

        return (
            <div className="carousel">
                <h1>In Theaters</h1>
                <div className="carousel-container">
                    {
                        this.state.moviesDisplayed.map(movie => (
                            <CarouselItem bgUrl="https://media0ch-a.akamaihd.net/14/80/3e89c3b546493a16a9b569006ee64604.jpeg" />
                        ))
                    }
                </div>
            </div>
        )
    }
}

export default Carousel;
