import React, {Component} from 'react';
import axios from 'axios';

const MovieNightContext = React.createContext();

export class Provider extends Component {

    handleGetMovies = (component) => {

        axios.get(component.props.endpoint)
            .then(response => {
                 component.setState({
                    movies: response.data.results
                });
            })
            .catch(error => {
              console.log('Error fetching and parsing data', error);
            });

    }

    render() {
        return(
          <MovieNightContext.Provider value={{
            actions: {
                 getMovies: this.handleGetMovies
            }
          }}>

          {this.props.children}

          </MovieNightContext.Provider>
        );
    }
}

export const Consumer = MovieNightContext.Consumer;
