import React, {Component} from 'react';
import CarouselItem from './CarouselItem';
import Slider from "react-slick";

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
    }

    render() {
        const settings = {
          dots: true,
          infinite: false,
          speed: 500,
          slidesToShow: 8,
          slidesToScroll: 8,
          accesibility: true,
          adaptiveHeight: false,
          arrows: true,
          autoplay:false,
          centerMode: false,
          centerPadding: "50px",
          cssEase: "ease",
          easing: "linear",
          edgeFriction: 0.35,
          fade: false,
          focusOnSelect: false,
          initialSlide: 0,
          swipe: true,

        };
        return (
            <div className="carousel-container">
                <h1> Multiple items </h1>
                <Slider {...settings}>

                </Slider>
              </div>
        );
    }
}

export default Carousel;
