import React, { Component } from "react";
import Slider from "react-slick";

class Trending extends Component {

    constructor() {
        super();
        this.state = {
          data: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getData(this, "https://api.themoviedb.org/3/trending/movie/day?api_key=9512b36f031887e7c9ad226e2c26a6b2");
        this.setState({ data: this.state.data.splice(0, 5) })
    }

  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      speed: 2500,
      autoplaySpeed: 5000,
      arrows: false
    };
    return (
      <div className="trending-container">
        <Slider {...settings}>
          <div className="trending-item">
            <img 
                className="trending-image"
                src="https://image.tmdb.org/t/p/w1280/ivhK1NjnFji6lD5dkAThSzd3Mgq.jpg"
            />
          </div>
          <div className="trending-item">
            <img 
                className="trending-image"
                src="https://image.tmdb.org/t/p/w1280/ivhK1NjnFji6lD5dkAThSzd3Mgq.jpg"
            />
          </div>
          <div className="trending-item">
            <img 
                className="trending-image"
                src="https://image.tmdb.org/t/p/w1280/ivhK1NjnFji6lD5dkAThSzd3Mgq.jpg"
            />
          </div>
        </Slider>
      </div>
    );
  }
}

export default Trending;
