import React, {Component} from 'react';
import CarouselItem from './CarouselItem';
import Slider from "react-slick";

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: [],
          n: 8,
          moviesDisplayed: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
        this.handleDisplayMovies(0);
    }

    handleDisplayMovies = (beginning) => {
        for (let i = beginning; i < this.state.n; i++) {
            this.state.moviesDisplayed.push(this.state.movies[i]); 
        }
    }

    render() {
        const settings = {
          dots: false,
          infinite: true,
          speed: 500,
          slidesToShow: 8,
          slidesToScroll: 8
        };
        return (
            /*<div className="carousel">
                <h1>In Theaters</h1>
                <div className="carousel-container">
                    {
                        this.state.moviesDisplayed.map( (movie) => 
                            <CarouselItem
                                bgUrl={"https://image.tmdb.org/t/p" + movie.poster_path}
                                n={this.state.n}
                            />
                        )
                    }
                </div>
            </div>*/

            <div>
                <h2> Multiple items </h2>
                <Slider {...settings}>
                  <div>
                    <h3>1</h3>
                  </div>
                  <div>
                    <h3>2</h3>
                  </div>
                  <div>
                    <h3>3</h3>
                  </div>
                  <div>
                    <h3>4</h3>
                  </div>
                  <div>
                    <h3>5</h3>
                  </div>
                  <div>
                    <h3>6</h3>
                  </div>
                  <div>
                    <h3>7</h3>
                  </div>
                  <div>
                    <h3>8</h3>
                  </div>
                  <div>
                    <h3>9</h3>
                  </div>
                </Slider>
              </div>

        );
    }
}

export default Carousel;
