import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
      movies: []
    }
  }

  handleGetMovies = () => {
    return true;
  }

  componentDidMount = () => {
    axios.get('https://api.themoviedb.org/3/movie/now_playing?api_key=9512b36f031887e7c9ad226e2c26a6b2&language=en-US&page=1')
    .then(response => {
      this.setState({
        movies: response.data.results
      });
    })
    .catch(error => {
      console.log('Error fetching and parsing data', error);
    }); 
    }

  render() {
    return (
      <div className="App">
        <div className="carousel">
        <h1>In Theaters</h1>
        <div className="item">
        </div>
        </div>
      </div>
    );
  }
}

export default App;
