import React, {Component} from 'react';
import CarouselItem from './CarouselItem';

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
    }

    render() {

        return (
            <div className="carousel">
                <h1>In Theaters</h1>
                <CarouselItem bgUrl={this.state.movies[0]} />
            </div>
        )
    }
}

export default Carousel;
