import React from 'react';

const CarouselItem = ({movie}) => {
    return (
      <div className="carousel-item">
        <img 
            className="poster"
            src={"https://image.tmdb.org/t/p/w185_and_h278_bestv2" + movie.poster_path}
            alt={movie.title}
        />
        <div
          style={{
            padding: "15px 5px",
            content:'9.5',
            width: "100%",
            height: "100%",
            top: 0,
            left: 0,
            background: "rgba(0,0,0,0.3)"
          }}
        >
        </div>
      </div>
    );
}

export default CarouselItem;
