import React, {Component} from 'react';
import CarouselItem from './CarouselItem';
import Slider from "react-slick";

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
    }

    render() {
        const settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 3,
          slidesToScroll: 3
        };
        return (
            <div className="carousel-container">
                <h2> Multiple items </h2>
                <Slider {...settings}>
                  <div className="carousel-item">
                    <h3>1</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>2</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>3</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>4</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>5</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>6</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>7</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>8</h3>
                  </div>
                  <div className="carousel-item">
                    <h3>9</h3>
                  </div>
                </Slider>
              </div>
        );
    }
}

export default Carousel;
