import React, {Component} from 'react';
import CarouselItem from './CarouselItem';
import Slider from "react-slick";

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: [],
          n: 8,
          moviesDisplayed: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
        this.handleDisplayMovies(0);
    }

    handleDisplayMovies = (beginning) => {
        for (let i = beginning; i < this.state.n; i++) {
            this.state.moviesDisplayed.push(this.state.movies[i]); 
        }
    }

    render() {
        const settings = {
          dots: false,
          infinite: true,
          speed: 500,
          slidesToShow: 8,
          slidesToScroll: 8
        };
        return (
            <div className="carousel">
                <h1>In Theaters</h1>
                <div className="carousel-container">
                    <Slider {...settings}>
                    {
                        this.state.moviesDisplayed.map( (movie) => 
                            <CarouselItem
                                bgUrl={"https://vignette.wikia.nocookie.net/batman/images/b/bb/TDKR_Batman_poster-1.jpg/revision/latest?cb=20120523032614"}
                                n={this.state.n}
                            />
                        )
                    }
                    </Slider>
                </div>
            </div>

        );
    }
}

export default Carousel;
