import React, {Component} from 'react';
import CarouselItem from './CarouselItem';
import Slider from "react-slick";

class Carousel extends Component {
    constructor() {
        super();
        this.state = {
          movies: []
        }
    }

    //Methods
    componentDidMount = () => {
        this.props.getMovies(this, this.props.endpoint);
        this.handleDisplayMovies(0);
    }

    handleDisplayMovies = (beginning) => {
        for (let i = beginning; i < this.state.movies; i++) {
            this.state.moviesDisplayed.push(this.state.movies[i]); 
        }
    }

    render() {
        const settings = {
          dots: false,
          infinite: true,
          speed: 500,
          slidesToShow: this.state.n,
          slidesToScroll: this.state.n
        };
        return (
            <div className="carousel">
                <h1>In Theaters</h1>
                <div className="carousel-container">
                    <Slider {...settings}>
                        <div className="carousel-item">0</div>
                        <div className="carousel-item">1</div>
                        <div className="carousel-item">2</div>
                        <div className="carousel-item">3</div>
                        <div className="carousel-item">4</div>
                        <div className="carousel-item">5</div>
                        <div className="carousel-item">6</div>
                        <div className="carousel-item">7</div>
                        <div className="carousel-item">8</div>
                        <div className="carousel-item">9</div>
                        <div className="carousel-item">10</div>
                        <div className="carousel-item">11</div>
                    </Slider>
                </div>
            </div>

        );
    }
}

export default Carousel;
